fun main(args: Array<String>) {
    val instruction = { c: Char, floor: Int -> if (c == '(') floor + 1 else floor - 1 }
    val rightFloor = input.toList().foldRight(0, instruction)
    println("Right floor $rightFloor")

    var position = 0
    var floor = 0
    for (c in input.toList()) {
        position++
        floor = instruction(c, floor)
        if (floor == -1) {
            break
        }
    }
    println("Position of first instruction that leads to basement: $position")
}
